<script>
import { portfolio, investasiList } from '../../assets/js/dummyData'
export default {
    data () {
      return {
        portfolio,
        investasiList
      }
    },
    computed: {
      onProcess(){
        let arr = this.portfolio.filter(field => field.status == "on_process")
        return arr
      },
      onDone(){
        let arr = this.portfolio.filter(field => field.status == "done")
        return arr
      }
    },
    methods:{
      filterPerusahaan(id){
        let arr = this.investasiList.filter(field => id == field.id)
        return arr[0].nama+' '+arr[0].identitas.detil_usaha
      }
    }
}
